import React from "react";
import { Link } from "react-router-dom";
import { connect } from "react-redux";
import { selectMovie } from "../actions";

const MovieList = (props) => {
  const renderList = () => {
    console.log(props);
    return props.movies.map((movie) => {
      console.log(movie.title);
      return (
        <div className="image-card">
          <img src={movie.url} alt="movie poster" />
          <div className="details">
            <Link to={`/movies/${movie.title}`} key={movie.id}>
              <h5
                className="title"
                onClick={(movie) => {
                  props.selectMovie(movie);
                }}
              >
                {movie.title}
              </h5>
              <h6 className="language">{movie.language}</h6>
            </Link>
          </div>
        </div>
      );
    });
  };
  return (
    <div className="content">
      <div className="container">
        <h2 className="heading">Premiers</h2>
        {renderList()}
        <div className="clear"></div>
      </div>
    </div>
  );
};

const mapStateToProps = (state) => {
  return { movies: state.movies };
};

export default connect(mapStateToProps, { selectMovie })(MovieList);
